	private String modifyHtmlString(String html, BillModel billModel) {
		String modifiedString = html;

		modifiedString = modifiedString.replace("${PARTY_NAME}", billModel.getPartyName());
		modifiedString = modifiedString.replace("${PARTY_ADDRESS}", billModel.getPartyAddress());

		modifiedString = modifiedString.replace("${INVOICE_NUMBER}", billModel.getInvoiceNumber());
		modifiedString = modifiedString.replace("${DATE}", billModel.getDate());
		modifiedString = modifiedString.replace("${GST_NUMBER}", billModel.getGstNumber());
		modifiedString = modifiedString.replace("${PLACE_SUPPLY}", billModel.getPlaceOfSupply());
		modifiedString = modifiedString.replace("${SUB_TOTAL}", billModel.getSubTotal().toPlainString());
		modifiedString = modifiedString.replace("${CGST}", billModel.getCgst().toPlainString());
		modifiedString = modifiedString.replace("${SGST}", billModel.getSgst().toPlainString());
		modifiedString = modifiedString.replace("${AMOUNT_IN_WORD}", billModel.getAmountInWord());
		modifiedString = modifiedString.replace("${GRAND_TOTAL}", billModel.getGrandTotal().toPlainString());
		modifiedString = modifiedString.replace("${FINANCIAL_YEAR}", billModel.getFinancialYear());
		modifiedString = modifiedString.replace("${GSET_PERCENTAGE}", billModel.getGstPercentage());

		StringBuilder productString = new StringBuilder();
		for (ProductDetails details : billModel.getProductDetails()) {

			productString.append("<div style=\"display: flex; margin-left: -647px; margin-top: 10px; width: 718px;\">");
			productString.append(
					"<div style=\"padding: 0; width: 36px; font-size: 10px; padding: 0; text-align: center; margin-left: -1px;\">"
							+ details.getSrNo() + "</div>\r\n"
							+ "					<div style=\"padding: 0; font-size: 12px; width: 341px; margin-left: 2px;\"> "
							+ details.getNameOfMsterial() + "</div>\r\n" + "					<div\r\n"
							+ "						style=\"padding: 0; width: 83px; text-align: center; font-size: 12px;\">"
							+ details.getHsnSac() + "</div>\r\n" + "					<div\r\n"
							+ "						style=\"padding: 0; width: 78px; text-align: center; font-size: 12px;\">"
							+ details.getQuantity() + "</div>\r\n" + "					<div\r\n"
							+ "						style=\"padding: 0; width: 61px; text-align: center; font-size: 12px; margin-left: 2px;\">"
							+ details.getRate() + "</div>\r\n" + "					<div\r\n"
							+ "						style=\"padding: 0; width: 40px; text-align: center; font-size: 12px;\">"
							+ details.getGst() + "</div>\r\n" + "					<div\r\n"
							+ "						style=\"padding: 0; width: 74px; font-size: 14px; text-align: center; font-size: 12px; margin-left: 2px;\">"
							+ details.getAmount() + "</div>");

			productString.append("</div>");
		}

		modifiedString = modifiedString.replace("${MATERIAL_DETAILS}", productString.toString());
		return modifiedString;
	}

	
	@GetMapping("/file/{fileName:.+}")
	public byte[] getFile(@PathVariable String fileName) {
		try {
			File file = new File("D:\\Bills\\" +fileName);
			return Files.readAllBytes(file.toPath());
		} catch (Exception e) {
			System.out.println(e.getMessage());
			return null;
		}
	}
	
	@GetMapping("/getSuggestions/{name}")
	public List<String> getExcelDetails(@PathVariable("name") String name) {
		try {
			List<ClientDetails> clientDetails = getClientDetails();
			return clientDetails.stream().filter(i -> i.getPartyName().toLowerCase().contains(name.toLowerCase())).map(i -> i.getPartyName()).collect(Collectors.toList());
		} catch (Exception e) {
			e.printStackTrace();
			return new ArrayList<>();
		}
	}
	
	@GetMapping("/getClientDetails/{name}")
	public Optional<ClientDetails> getClientDetails(@PathVariable("name") String name) {
		try {
			List<ClientDetails> clientDetails = getClientDetails();
			return clientDetails.stream().filter(i -> i.getPartyName().equalsIgnoreCase(name)).findFirst();
		} catch (Exception e) {
			return null;
		}
	}
	
	public List<ClientDetails> getClientDetails() {
		try {
			List<ClientDetails> clientDetailsList = new ArrayList<>();
			Workbook workbook = WorkbookFactory.create(new File("Clients.xlsx"));
			if (workbook !=  null) {
				System.out.println(workbook.getNumberOfSheets());
				DataFormatter dataFormatter = new DataFormatter();
				
				Iterator<Sheet> sheetIterator = workbook.sheetIterator();
				while (sheetIterator.hasNext()) {
					Sheet sheet = sheetIterator.next();
//					System.out.println(sheet.getSheetName());
					
					for (Row row: sheet) {
						if (row.getRowNum() != 0) {
//							System.out.println(dataFormatter.formatCellValue(row.getCell(0)));
//							System.out.println(dataFormatter.formatCellValue(row.getCell(0)).toLowerCase().contains("test"));
							ClientDetails clientDetails = new ClientDetails();
							clientDetails.setPartyName(dataFormatter.formatCellValue(row.getCell(0)).trim());
							clientDetails.setPartyAddress(dataFormatter.formatCellValue(row.getCell(1)).trim());
							clientDetails.setPartyGstNumber(dataFormatter.formatCellValue(row.getCell(2)).trim());
							clientDetails.setPlaceOfSupply(dataFormatter.formatCellValue(row.getCell(3)).trim());
							clientDetailsList.add(clientDetails);
						}
					}
					
				}
			}
			return clientDetailsList;
		} catch (Exception e) {
			return new ArrayList<>();
		}
	}
